  assert(reverseSentence("  Leading spaces") == "spaces Leading");
  assert(reverseSentence("Trailing spaces   ") == "spaces Trailing");
  assert(reverseSentence("Multiple   between   words") == "words between Multiple");
  assert(reverseSentence("") == "");
  assert(reverseSentence("   ") == "");
  assert(reverseSentence("SingleWord") == "SingleWord");
}

void main() {
  testReverseSentence();
  print("All tests passed!");
}

