int maxCount(int m, int n, List<List<int>> ops) {
  // If there are no operations, all cells contain the maximum integer (0)
  if (ops.isEmpty) {
    return m * n;
  }

  // Find the minimum a and b values across all operations
  int minA = ops[0][0];
  int minB = ops[0][1];

  for (var op in ops) {
    if (op[0] < minA) minA = op[0];
    if (op[1] < minB) minB = op[1];
  }

  return minA * minB;
}

assertions:
