import 'dart:io';

// analyzeWordFrequency analyzes the frequency of words in a given text string.
// It removes punctuation, converts words to lowercase, and counts occurrences.
// Returns a map where keys are words and values are their frequencies.
Map<String, int> analyzeWordFrequency(String text) {
  final wordCount = <String, int>{};
  if (text.isEmpty) {
    return wordCount;
  }

  // Remove punctuation and make lowercase
  final punctuation = RegExp(r'[^\w\s]');
  final cleanedText = text.replaceAll(punctuation, '').toLowerCase();
  final words = cleanedText.split(RegExp(r'\s+'));

  for (final word in words) {
    if (word.isEmpty) continue;
    wordCount.update(word, (value) => value + 1, ifAbsent: () => 1);
  }

  return wordCount;
}

