    analyzeWordFrequency("To be, or not to be: that is the question.").toString() == 
    '{to: 2, be: 2, or: 1, not: 1, that: 1, is: 1, the: 1, question: 1}'
  );

  // Test case 4 (empty string)
  assert(
    analyzeWordFrequency("").toString() == '{}'
  );

  // Test case 5 (punctuation)
  assert(
    analyzeWordFrequency("Punctuation! Test? This, is. A: test; with-lots of punctuation.").toString() == 
    '{punctuation: 2, test: 2, this: 1, is: 1, a: 1, withlots: 1, of: 1}'
  );

  // Test case 6 (multiline)
