// analyzeLogs processes a list of log entries to determine the number of unique users
// for each action. Each log entry is expected to be in the format "action ... user_id".
// Returns a map where keys are actions and values are counts of unique users for each action.
Map<String, int> analyzeLogs(List<String> logs) {
  final Map<String, Set<String>> actionUsers = {};

  for (final log in logs) {
    final parts = log.split(' ');
    if (parts.length < 2) continue; // Skip malformed entries

    final action = parts[0];
    final userId = parts.last;

    // Initialize the set if this action hasn't been seen before
    actionUsers.putIfAbsent(action, () => <String>{});

    // Add the user to this action's set
    actionUsers[action]!.add(userId);
  }

  // Convert sets of users to counts
  return {
    for (final entry in actionUsers.entries)
      entry.key: entry.value.length
  };
}

