import 'dart:collection';

int reachableVertices(int n, int m, List<List<int>> edges, int v, int k) {
  // Build the adjacency list representation of the graph
  Map<int, List<int>> graph = {};
  for (var edge in edges) {
    int u = edge[0];
    int w = edge[1];
    if (!graph.containsKey(u)) {
      graph[u] = [];
    }
    graph[u]!.add(w);
  }

  // Initialize BFS queue with the starting vertex and step count 0
  Queue<List<int>> queue = Queue();
  queue.add([v, 0]);

  // Track visited vertices at each step level
  Map<int, Set<int>> visited = {};

  while (queue.isNotEmpty) {
    List<int> current = queue.removeFirst();
    int vertex = current[0];
    int steps = current[1];

    if (steps == k) {
      if (!visited.containsKey(steps)) {
        visited[steps] = {};
      }
      visited[steps]!.add(vertex);
    } else if (steps < k) {
      if (graph.containsKey(vertex)) {
        for (int neighbor in graph[vertex]!) {
          if (!visited.containsKey(steps + 1) || !visited[steps + 1]!.contains(neighbor)) {
            if (!visited.containsKey(steps + 1)) {
              visited[steps + 1] = {};
            }
            visited[steps + 1]!.add(neighbor);
            queue.add([neighbor, steps + 1]);
          }
        }
      }
    }
  }

  return visited.containsKey(k) ? visited[k]!.length : 0;
}

