import 'dart:collection'; // For heap operations (simulated using List and sort)

// MinHeap implementation in Dart
// This class provides a minimum heap data structure with the following operations:
// - insert: Adds a new key to the heap
// - extractMin: Removes and returns the smallest key
// - decreaseKey: Decreases the value of an existing key
// - getMin: Returns the smallest key without removing it
class MinHeap {
  List<int> _heap = [];
  Map<int, int> _keyMap = {};

  // Inserts a new key into the heap
  void insert(int key) {
    _heap.add(key);
    _keyMap[key] = key;
    _heap.sort(); // Simulate heap behavior by sorting
  }

  // Removes and returns the minimum key from the heap
  int extractMin() {
    if (_heap.isEmpty) throw StateError('Heap is empty');
    int minKey = _heap.removeAt(0);
    _keyMap.remove(minKey);
    return minKey;
  }

  // Decreases the value of an existing key to a new key
  void decreaseKey(int currentKey, int newKey) {
    if (!_keyMap.containsKey(currentKey)) {
      throw ArgumentError('Key not found in heap');
    }
    if (newKey > currentKey) {
      throw ArgumentError('New key must be smaller than current key');
    }

    int index = _heap.indexOf(currentKey);
    _heap[index] = newKey;
    _keyMap.remove(currentKey);
    _keyMap[newKey] = newKey;
    _heap.sort(); // Re-sort to maintain heap property
  }

  // Returns the minimum key without removing it
  int getMin() {
    if (_heap.isEmpty) throw StateError('Heap is empty');
    return _heap.first;
  }
}

