// isScramble checks if string s2 is a scrambled version of string s1.
// It uses a recursive approach to determine if the strings can be split and matched in any order.
bool isScramble(String s1, String s2) {
  // Base case: if lengths are different, they can't be scrambled versions
  if (s1.length != s2.length) {
    return false;
  }
  // Base case: if strings are identical, they are scrambled versions
  if (s1 == s2) {
    return true;
  }
  // Base case: if sorted characters are different, they can't be scrambled versions
  if (_sortString(s1) != _sortString(s2)) {
    return false;
  }

  int n = s1.length;
  for (int i = 1; i < n; i++) {
    // Check two possible splits:
    // 1. First i characters of s1 match first i of s2, and rest match
    // 2. First i characters of s1 match last i of s2, and rest match
    if ((isScramble(s1.substring(0, i), s2.substring(0, i)) &&
            isScramble(s1.substring(i), s2.substring(i))) ||
        (isScramble(s1.substring(0, i), s2.substring(n - i)) &&
            isScramble(s1.substring(i), s2.substring(0, n - i)))) {
      return true;
    }
  }

  return false;
}

// Helper function to sort characters in a string for comparison
String _sortString(String s) {
  var chars = s.split('');
  chars.sort();
  return chars.join();
}

assertions:
void testIsScramble() {
  assert(isScramble('great', 'rgeat') == true);
  assert(isScramble('abcde', 'caebd') == false);
  assert(isScramble('a', 'a') == true);
  assert(isScramble('ab', 'ba') == true);
  assert(isScramble('abcd', 'badc') == true);
  assert(isScramble('abcd', 'dacb') == true);
  assert(isScramble('abc', 'def') == false);
  assert(isScramble('abb', 'bba') == true);
}

void main() {
  testIsScramble();
}

