  assert(findLongestSubstring("abcabcbb") == "abc");
  assert(findLongestSubstring("bbbbb") == "b");
  assert(findLongestSubstring("pwwkew") == "wke");
  assert(findLongestSubstring("") == "");
  assert(findLongestSubstring("abcdef") == "abcdef");
  assert(findLongestSubstring("aab") == "ab");
  assert(findLongestSubstring("dvdf") == "vdf");
}

void main() {
  testFindLongestSubstring();
  print("All tests passed!");
}

