// performOperations executes a series of operations on an array of integers.
// It supports two operation types:
//   1. Multiply elements in a range by a given factor
//   2. Calculate the sum of elements in a range
// The function returns a list of results for all sum operations (type 2).
List<int> performOperations(int n, List<int> arr, List<List<int>> operations) {
  List<int> results = [];

  for (List<int> operation in operations) {
    int operationType = operation[0];

    if (operationType == 1) {
      // Multiplication operation: multiply elements from index l to r by x
      int l = operation[1];
      int r = operation[2];
      int x = operation[3];

      for (int i = l - 1; i < r; i++) {
        arr[i] *= x;
      }
    } else if (operationType == 2) {
      // Sum operation: calculate sum of elements from index l to r
      int l = operation[1];
      int r = operation[2];

      int sum = 0;
      for (int i = l - 1; i < r; i++) {
        sum += arr[i];
      }
      results.add(sum);
    }
  }

  return results;
}

assertions:
void testPerformOperations() {
  // Test case 1: Basic operations
  List<int> arr1 = [1, 2, 3, 4, 5];
  List<List<int>> operations1 = [
    [1, 2, 4, 2],  // Multiply indices 1-3 by 2
    [2, 1, 3],     // Sum indices 0-2
    [1, 1, 5, 3],  // Multiply all elements by 3
    [2, 2, 4]      // Sum indices 1-3
  ];
  assert(performOperations(arr1.length, arr1, operations1).toString() == [11, 54].toString());
  assert(arr1.toString() == [3, 12, 18, 24, 15].toString());

  // Test case 2: Only sum operations
  List<int> arr2 = [10, 20, 30, 40, 50];
  List<List<int>> operations2 = [
    [2, 1, 5],  // Sum all elements
    [2, 2, 4],  // Sum indices 1-3
    [2, 3, 3]   // Sum single element at index 2
  ];
  assert(performOperations(arr2.length, arr2, operations2).toString() == [150, 90, 30].toString());
  assert(arr2.toString() == [10, 20, 30, 40, 50].toString());

  // Test case 3: Only multiplication operations
  List<int> arr3 = [1, 1, 1, 1];
  List<List<int>> operations3 = [
    [1, 1, 4, 5],  // Multiply all by 5
    [1, 2, 3, 2],  // Multiply middle two by 2
    [1, 1, 1, 10]  // Multiply first by 10
  ];
  assert(performOperations(arr3.length, arr3, operations3).isEmpty);
  assert(arr3.toString() == [50, 10, 10, 5].toString());

  // Test case 4: Mixed operations with edge cases
  List<int> arr4 = [2, 0, 4, 1];
  List<List<int>> operations4 = [
    [1, 1, 4, 0],  // Multiply all by 0
    [2, 1, 4],     // Sum all (should be 0)
    [1, 2, 3, 5],  // Multiply middle two by 5
    [2, 2, 3],     // Sum middle two
    [2, 4, 4]      // Sum last element
  ];
  assert(performOperations(arr4.length, arr4, operations4).toString() == [0, 0, 0].toString());
  assert(arr4.toString() == [0, 0, 0, 0].toString());
}

void main() {
  testPerformOperations();
}

