  assert(lengthOfLongestNonNegativeSumSubarray(4, [-1, -2, -3, -4]) == 0);

  // Test case 3: All positive numbers
  assert(lengthOfLongestNonNegativeSumSubarray(5, [1, 2, 3, 4, 5]) == 5);

  // Test case 4: All zeros
  assert(lengthOfLongestNonNegativeSumSubarray(5, [0, 0, 0, 0, 0]) == 5);

  // Test case 5: Mixed case with multiple subarrays
  assert(lengthOfLongestNonNegativeSumSubarray(6, [1, -2, 1, 1, -2, 1]) == 3);
}

void main() {
  testLengthOfLongestNonNegativeSumSubarray();
  print('All tests passed!');
}

