// maxShips calculates the maximum number of ships that can be loaded with containers
// in strictly increasing order of weights, where each ship can carry at most k containers.
// The containers are sorted by weight first to ensure the order is maintained.
int maxShips(int n, int k, List<int> weights) {
  // Sort the weights to ensure they're in increasing order
  weights.sort();
  int ships = 0;
  int i = 0;

  // Each ship can take up to k containers
  while (i < n) {
    ships++;
    i += k;
  }

  return ships;
}

assertions:
void testMaxShips() {
  assert(maxShips(5, 2, [3, 1, 4, 1, 5]) == 3);
  assert(maxShips(3, 5, [7, 2, 4]) == 1);
  assert(maxShips(4, 1, [9, 3, 6, 1]) == 4);
  assert(maxShips(0, 3, []) == 0);
  assert(maxShips(9, 3, [5, 2, 8, 1, 3, 7, 4, 6, 9]) == 3);
}

void main() {
  testMaxShips();
}

