  assert(minMismatches(4, 3, ["AGT", "GGT", "TGT", "ATT"]) == 1);
  assert(minMismatches(3, 2, ["AA", "AA", "AA"]) == 0);
  assert(minMismatches(2, 3, ["ACG", "TGA"]) == 3);
  assert(minMismatches(4, 4, ["AAAA", "AAAT", "ATTT", "TTTT"]) == 1);
}

void main() {
  testMinMismatches();
}

