// minimumRemovalsToLuckyLine calculates the minimum number of students to remove
// so that the remaining students form a non-decreasing sequence by height.
// It uses dynamic programming to find the longest non-decreasing subsequence (LNDS),
// then calculates removals as total students minus LNDS length.
List<int> minimumRemovalsToLuckyLine(int t, List<List<dynamic>> testCases) {
  List<int> results = [];

  for (var caseData in testCases) {
    int n = caseData[0];
    List<int> heights = List<int>.from(caseData[1]);
    List<int> dp = List<int>.filled(n, 1);

    for (int i = 1; i < n; i++) {
      for (int j = 0; j < i; j++) {
        if (heights[i] >= heights[j]) {
          dp[i] = dp[i] > dp[j] + 1 ? dp[i] : dp[j] + 1;
        }
      }
    }

    int longestSubsequence = dp.reduce((a, b) => a > b ? a : b);
    results.add(n - longestSubsequence);
  }

  return results;
}

assertions:
void testMinimumRemovalsToLuckyLine() {
  List<List<dynamic>> testCases = [
    [5, [5, 4, 3, 2, 1]],  // All decreasing - need to remove 4
    [5, [1, 2, 3, 4, 5]],  // Already non-decreasing - remove 0
    [6, [2, 5, 3, 4, 6, 1]],  // Remove 2 (the 5 and 1 or other combinations)
    [4, [1, 1, 1, 1]],  // All equal - remove 0
    [3, [1, 2, 1]],  // Remove 1 (the last 1)
  ];

  List<int> expectedResults = [4, 0, 2, 0, 1];
  List<int> actualResults = minimumRemovalsToLuckyLine(testCases.length, testCases);

  for (int i = 0; i < testCases.length; i++) {
    assert(actualResults[i] == expectedResults[i]);
  }
}

void main() {
  testMinimumRemovalsToLuckyLine();
  print('All tests passed!');
}

