// calculateRanks determines the ranks of students based on their total marks (Math + Science),
// with Math marks as the first tiebreaker and student name as the second tiebreaker.
// Students are sorted in descending order of total marks, then descending Math marks,
// and finally ascending name order.
// Returns a list of tuples containing student names and their corresponding ranks.
List<MapEntry<String, int>> calculateRanks(List<List<dynamic>> students) {
  // Calculate total marks for each student and store in a new list
  List<List<dynamic>> processedStudents = [];
  for (var student in students) {
    String name = student[0];
    int mathMarks = student[1];
    int scienceMarks = student[2];
    int totalMarks = mathMarks + scienceMarks;
    processedStudents.add([name, mathMarks, scienceMarks, totalMarks]);
  }

  // Sort students by total marks (desc), math marks (desc), and name (asc)
  processedStudents.sort((a, b) {
    if (a[3] != b[3]) {
      return b[3].compareTo(a[3]); // Higher total comes first
    } else if (a[1] != b[1]) {
      return b[1].compareTo(a[1]); // Higher math comes first
    } else {
      return a[0].compareTo(b[0]); // Alphabetical name order
    }
  });

  // Determine ranks with proper handling of ties
  List<MapEntry<String, int>> result = [];
  int rank = 1;
  for (int i = 0; i < processedStudents.length; i++) {
    if (i == 0) {
      result.add(MapEntry(processedStudents[i][0], rank));
    } else {
      // Check if current student has same total and math marks as previous
      if (processedStudents[i][3] == processedStudents[i-1][3] && 
          processedStudents[i][1] == processedStudents[i-1][1]) {
        // Same rank as previous student
        result.add(MapEntry(processedStudents[i][0], result.last.value));
      } else {
        // New rank (current position + 1)
        rank = i + 1;
        result.add(MapEntry(processedStudents[i][0], rank));
      }
    }
  }

  return result;
}

assertions:
void testCalculateRanks() {
  // Test Case 1
  var testCase1 = [
    ["Alice", 90, 80],
    ["Bob", 85, 95],
    ["Charlie", 90, 80],
    ["David", 85, 90]
  ];
  var result1 = calculateRanks(testCase1);
  assert(result1[0].key == "Bob" && result1[0].value == 1);
  assert(result1[1].key == "David" && result1[1].value == 2);
  assert(result1[2].key == "Alice" && result1[2].value == 3);
  assert(result1[3].key == "Charlie" && result1[3].value == 3);

  // Test Case 2
  var testCase2 = [
    ["Eve", 100, 100],
    ["Frank", 100, 100],
    ["Grace", 99, 99]
  ];
  var result2 = calculateRanks(testCase2);
  assert(result2[0].key == "Eve" && result2[0].value == 1);
  assert(result2[1].key == "Frank" && result2[1].value == 1);
  assert(result2[2].key == "Grace" && result2[2].value == 3);

  // Test Case 3
  var testCase3 = [
    ["Xavier", 80, 70],
    ["Yvonne", 70, 80],
    ["Zack", 70, 70]
  ];
  var result3 = calculateRanks(testCase3);
  assert(result3[0].key == "Xavier" && result3[0].value == 1);
  assert(result3[1].key == "Yvonne" && result3[1].value == 2);
  assert(result3[2].key == "Zack" && result3[2].value == 3);
}

void main() {
  testCalculateRanks();
  print("All tests passed!");
}

