// generateSequence creates a decorative sequence by repeating each item in the input list
// with a count that doubles for each subsequent item (starting with N repetitions for the first item).
// For example, with N=2 and items ['A', 'B'], it returns ['A', 'A', 'B', 'B', 'B', 'B'].
List<String> generateSequence(int N, List<String> items) {
  List<String> sequence = [];
  int count = N;
  for (String item in items) {
    sequence.addAll(List<String>.filled(count, item));
    count *= 2;
  }
  return sequence;
}

// decorativeSequences processes multiple test cases to generate decorative sequences.
// Each test case consists of N (initial repeat count), M (unused in this implementation),
// and a list of items to be repeated in the sequence.
List<String> decorativeSequences(List<List<dynamic>> testCases) {
  List<String> results = [];
  for (List<dynamic> tc in testCases) {
    int N = tc[0] as int;
    // M is not used in the sequence generation, but kept for compatibility with the original
    List<String> items = (tc[2] as List).cast<String>();
    List<String> sequence = generateSequence(N, items);
    results.add(sequence.join(' '));
  }
  return results;
}

assertions:
void testDecorativeSequences() {
  List<List<dynamic>> testCases = [
    [2, 0, ['A', 'B']],          // Test case 1: Start with 2 repeats, double each time
    [1, 0, ['X', 'Y', 'Z']],     // Test case 2: Start with 1 repeat
    [3, 0, ['Hello', 'World']],  // Test case 3: Start with 3 repeats
    [1, 0, []]                   // Test case 4: Empty items list (edge case)
  ];

  List<String> results = decorativeSequences(testCases);

  assert(results[0] == 'A A B B B B');
  assert(results[1] == 'X Y Y Z Z Z Z');
  assert(results[2] == 'Hello Hello Hello World World World World World World');
  assert(results[3] == '');
}

void main() {
  testDecorativeSequences();
}

