double maxSumSubarray(List<int> arr, int l, int r) {
  int n = arr.length;

  // Initialize the maximum sum variable
  double maxSum = double.negativeInfinity;

  // Iterate through all possible subarray lengths from l to r
  for (int length = l; length <= r; length++) {
    // Calculate the sum of the first subarray of the current length
    double currentSum = arr.sublist(0, length).reduce((a, b) => a + b).toDouble();
    if (currentSum > maxSum) {
      maxSum = currentSum;
    }

    // Use sliding window technique to calculate the sum of the remaining subarrays
    for (int i = length; i < n; i++) {
      currentSum += arr[i] - arr[i - length];
      if (currentSum > maxSum) {
        maxSum = currentSum;
      }
    }
  }

  return maxSum;
}

assertions:
