  // Test case 3 (edge case: all negative numbers)
  assert(maxSumSubarray([-2, -3, -1, -4, -5], 1, 3) == -1.0);

  // Test case 4 (edge case: single element array)
  assert(maxSumSubarray([5], 1, 1) == 5.0);
}

