// hasDuplicates checks if a string contains any duplicate characters.
// It uses a Set to track seen characters and returns true if any character appears more than once.
// The function assumes the input string contains only lowercase English letters.
bool hasDuplicates(String s) {
  Set<String> seenCharacters = {};
  for (int i = 0; i < s.length; i++) {
    String char = s[i];
    if (seenCharacters.contains(char)) {
      return true;
    }
    seenCharacters.add(char);
  }
  return false;
}

