// canVisitAllPeaks determines if all peaks in a mountain range can be visited from any other peak
// with at most 2 steps. It uses the Floyd-Warshall algorithm to compute shortest paths between
// all pairs of peaks and checks if any path exceeds 2 steps.
String canVisitAllPeaks(int n, int m, List<List<int>> paths) {
  // Initialize distance matrix with infinity and 0 for same vertex pairs
  List<List<double>> dist = List.generate(n, (_) => List.filled(n, double.infinity));
  for (int i = 0; i < n; i++) {
    dist[i][i] = 0;
  }

  // Populate initial distances from given paths
  for (List<int> path in paths) {
    int i = path[0] - 1;
    int j = path[1] - 1;
    dist[i][j] = 1;
    dist[j][i] = 1;
  }

  // Floyd-Warshall algorithm to find shortest paths
  for (int k = 0; k < n; k++) {
    for (int i = 0; i < n; i++) {
      for (int j = 0; j < n; j++) {
        if (dist[i][j] > dist[i][k] + dist[k][j]) {
          dist[i][j] = dist[i][k] + dist[k][j];
        }
      }
    }
  }

  // Check if any path exceeds 2 steps
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      if (dist[i][j] > 2) {
        return "NO";
      }
    }
  }

  return "YES";
}

assertions:
void testCanVisitAllPeaks() {
  // Test case 1: Linear connection - should be reachable
  assert(canVisitAllPeaks(3, 2, [
    [1, 2],
    [2, 3]
  ]) == "YES");

  // Test case 2: Longer linear connection - some peaks too far
  assert(canVisitAllPeaks(4, 3, [
    [1, 2],
    [2, 3],
    [3, 4]
  ]) == "NO");

  // Test case 3: Highly connected - should be reachable
  assert(canVisitAllPeaks(5, 6, [
    [1, 2],
    [2, 3],
    [3, 4],
    [4, 5],
    [1, 3],
    [3, 5]
  ]) == "YES");

  // Test case 4: Disconnected components - should fail
  assert(canVisitAllPeaks(4, 2, [
    [1, 2],
    [3, 4]
  ]) == "NO");
}

void main() {
  testCanVisitAllPeaks();
  print("All tests passed!");
}

