  assert(maxKnights(2, 3) == 3);
  assert(maxKnights(4, 4) == 8);
  assert(maxKnights(1, 8) == 8);
  assert(maxKnights(3, 2) == 3);
  assert(maxKnights(5, 5) == 13);

  // Test the solve function with batch processing
  List<List<int>> testCases = [
    [1, 1], [2, 3], [4, 4], [1, 8], [3, 2], [5, 5]
  ];
  List<int> expectedResults = [1, 3, 8, 8, 3, 13];
  assert(solve(testCases).toString() == expectedResults.toString());
}

