// isPathExist checks if there's a path between source and target vertices in a graph.
// It uses Depth-First Search (DFS) to traverse the graph and find the path.
// vertexCount: total number of vertices in the graph
// edges: list of directed edges as pairs of vertices
// source: starting vertex
// target: destination vertex
// Returns true if a path exists, false otherwise.
bool isPathExist(int vertexCount, List<List<int>> edges, int source, int target) {
  if (source == target) {
    return true;
  }

  // Build adjacency list representation of the graph
  Map<int, List<int>> graph = {};
  for (int i = 0; i < vertexCount; i++) {
    graph[i] = [];
  }
  for (List<int> edge in edges) {
    int u = edge[0];
    int v = edge[1];
    graph[u]!.add(v);
  }

  // DFS function to traverse the graph
  bool dfs(int node, Set<int> visited) {
    if (node == target) {
      return true;
    }
    visited.add(node);
    for (int neighbor in graph[node]!) {
      if (!visited.contains(neighbor)) {
        if (dfs(neighbor, visited)) {
          return true;
        }
      }
    }
    return false;
  }

  return dfs(source, <int>{});
}

assertions:
void testIsPathExist() {
  // Test case 1: Simple path exists
  assert(isPathExist(3, [
    [0, 1],
    [1, 2]
  ], 0, 2) == true);

  // Test case 2: No path exists (disconnected components)
  assert(isPathExist(4, [
    [0, 1],
