// Checks if all planets ('.' cells) in an n x n grid are connected.
// Uses Depth-First Search (DFS) to explore connected components.
// Returns "YES" if all planets are connected, "NO" otherwise.
String isAllPlanetsConnected(int n, List<List<String>> grid) {
  // Nested DFS function to mark connected cells
  void dfs(int x, int y) {
    if (x < 0 || y < 0 || x >= n || y >= n || grid[x][y] != '.') {
      return;
    }
    grid[x][y] = '#'; // Mark as visited
    // Explore adjacent cells
    dfs(x + 1, y);
    dfs(x - 1, y);
    dfs(x, y + 1);
    dfs(x, y - 1);
  }

  int components = 0;
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      if (grid[i][j] == '.') {
        if (components > 0) {
          // Found another component - not all connected
          return "NO";
        }
        dfs(i, j);
        components++;
      }
    }
  }
  return components == 1 ? "YES" : "NO";
}

assertions:
