int maxSimultaneousSessions(List<List<int>> sessions) {
  // Create a list to hold all events (start and end points)
  List<List<dynamic>> events = [];

  // Convert each session into start and end events
  for (List<int> session in sessions) {
    int start = session[0];
    int end = session[1];
    events.add([start, 'start']);
    events.add([end + 1, 'end']);  // end + 1 to make intervals inclusive
  }

  // Sort events by time, with 'end' events coming before 'start' at the same time
  events.sort((a, b) {
    if (a[0] == b[0]) {
      // For same time, 'end' comes before 'start'
      return a[1].compareTo(b[1]);
    }
    return a[0].compareTo(b[0]);
  });

  int currentSessions = 0;
  int maxSessions = 0;

  // Process each event to track session count
  for (List<dynamic> event in events) {
    if (event[1] == 'start') {
      currentSessions++;
      if (currentSessions > maxSessions) {
        maxSessions = currentSessions;
      }
    } else {
      currentSessions--;
    }
  }

  return maxSessions;
}

assertions:
void testMaxSimultaneousSessions() {
  assert(maxSimultaneousSessions([[1, 5], [3, 7], [2, 4]]) == 3);
  assert(maxSimultaneousSessions([[1, 2], [3, 4], [5, 6]]) == 1);
  assert(maxSimultaneousSessions([[1, 10], [2, 3], [4, 5], [6, 7]]) == 2);
  assert(maxSimultaneousSessions([[1, 5], [5, 10]]) == 2);
  assert(maxSimultaneousSessions([]) == 0);
  assert(maxSimultaneousSessions([[1, 1]]) == 1);
  assert(maxSimultaneousSessions([[1, 5], [1, 5], [1, 5]]) == 3);
}

void main() {
