// countVowels takes a list of strings and returns a map with the strings as keys
// and the number of vowels in each string as values. The counting is case-insensitive.
Map<String, int> countVowels(List<String> strings) {
  // Helper function to count vowels in a single string
  int vowelCount(String s) {
    const vowels = 'aeiou';
    int count = 0;
    for (var char in s.toLowerCase().runes) {
      if (vowels.contains(String.fromCharCode(char))) {
        count++;
      }
    }
    return count;
  }

  // Create the result map by applying vowelCount to each string
  final result = <String, int>{};
  for (var s in strings) {
    result[s] = vowelCount(s);
  }
  return result;
}

