  assert(minChanges('BA') == 0);
  assert(minChanges('AAB') == 1);
  assert(minChanges('BBA') == 1);
  assert(minChanges('ABA') == 0);
  assert(minChanges('BAB') == 0);
  assert(minChanges('AAAA') == 2);
  assert(minChanges('BBBB') == 2);
  assert(minChanges('ABAB') == 0);
  assert(minChanges('BABA') == 0);
}

void main() {
  testMinChanges();
}

