import 'dart:collection';

// countWords counts the frequency of each word in the given text.
// It splits the text into words, converts them to lowercase, and removes punctuation
// from the start and end of each word before counting.
// Returns a Map where keys are words and values are their respective counts.
Map<String, int> countWords(String text) {
  // Split the text into words
  List<String> words = text.split(' ');

  // Clean each word by converting to lowercase and removing punctuation
  List<String> cleanedWords = [];
  for (String word in words) {
    String cleaned = word.toLowerCase();
    cleaned = cleaned.replaceAll(RegExp(r'^[^\w]+|[^\w]+$'), '');
    cleanedWords.add(cleaned);
  }

  // Count word frequencies using a HashMap
  Map<String, int> wordCounts = HashMap();
  for (String word in cleanedWords) {
    wordCounts.update(word, (value) => value + 1, ifAbsent: () => 1);
  }

  return wordCounts;
}

