// quickSelect finds the kth smallest element in a list using the Quickselect algorithm.
// If k is out of bounds (less than 1 or greater than list length), it returns null.
// The algorithm works by partially sorting the list around a pivot until the kth element is found.
dynamic quickSelect(List<int> arr, int k) {
  // Check if k is out of bounds
  if (k < 1 || k > arr.length) {
    return null;
  }

  // Helper function to partition the list around a pivot
  int partition(int left, int right, int pivotIndex) {
    int pivotValue = arr[pivotIndex];
    // Move pivot to end
    _swap(arr, pivotIndex, right);
    int storeIndex = left;

    for (int i = left; i < right; i++) {
      if (arr[i] < pivotValue) {
        _swap(arr, storeIndex, i);
        storeIndex++;
      }
    }
    // Move pivot to its final place
    _swap(arr, right, storeIndex);
    return storeIndex;
  }

  // Recursive selection function
  int select(int left, int right, int kSmallest) {
    if (left == right) {
      return arr[left];
    }

    int pivotIndex = (left + right) ~/ 2;
    pivotIndex = partition(left, right, pivotIndex);

    if (kSmallest == pivotIndex) {
      return arr[kSmallest];
    } else if (kSmallest < pivotIndex) {
      return select(left, pivotIndex - 1, kSmallest);
    } else {
      return select(pivotIndex + 1, right, kSmallest);
    }
  }

  return select(0, arr.length - 1, k - 1);
}

// Helper function to swap elements in a list
void _swap(List<int> arr, int i, int j) {
  int temp = arr[i];
  arr[i] = arr[j];
  arr[j] = temp;
}

