// minBitFlipsToEqualize calculates the minimum number of bit flips needed to make
// the number of 0s and 1s equal in a binary string.
// If it's not possible to equalize them (when the difference is odd), returns -1.
int minBitFlipsToEqualize(int n, String binaryString) {
  int count0 = binaryString.split('').where((c) => c == '0').length;
  int count1 = n - count0;

  if ((count0 - count1).abs() % 2 != 0) {
    return -1;
  } else {
    return (count0 - count1).abs() ~/ 2;
  }
}

// processTestCases handles multiple test cases by applying minBitFlipsToEqualize
// to each case and collecting the results in a list.
List<int> processTestCases(int t, List<Map<String, dynamic>> testCases) {
  List<int> results = [];
  for (int i = 0; i < t; i++) {
    int n = testCases[i]['n'];
    String binaryString = testCases[i]['binaryString'];
    results.add(minBitFlipsToEqualize(n, binaryString));
  }
  return results;
}

