import 'dart:core';

// Finds the sentence with the most unique words in a given text.
// Sentences are separated by '.', '!', or '?'.
// The function ignores case and punctuation.
// Returns:
//   - The sentence with most unique words if found
//   - Error message if input is invalid or no sentences found
String sentenceWithMostUniqueWords(String text) {
  if (text.trim().isEmpty) {
    return "Error: Input string is empty.";
  }

  // Split text into sentences using punctuation marks as delimiters
  List<String> sentences = text.split(RegExp(r'[.!?]'));
  int maxUniqueWords = 0;
  String resultSentence = "";

  for (String sentence in sentences) {
    // Extract words (ignoring punctuation and case)
    List<String> words = RegExp(r'\b\w+\b')
        .allMatches(sentence.toLowerCase())
        .map((match) => match.group(0)!)
        .toList();

    // Get unique words
    Set<String> uniqueWords = words.toSet();

    // Update result if current sentence has more unique words
    if (uniqueWords.length > maxUniqueWords) {
      maxUniqueWords = uniqueWords.length;
      resultSentence = sentence.trim();
    }
  }

  return resultSentence.isNotEmpty 
      ? resultSentence 
      : "Error: No valid sentences found in the input text.";
}

assertions:
void testSentenceWithMostUniqueWords() {
  assert(sentenceWithMostUniqueWords('Hello world. This is a test. Testing, testing! Works?') == 
      'This is a test');
  assert(sentenceWithMostUniqueWords('Single sentence') == 
      'Single sentence');
  assert(sentenceWithMostUniqueWords('Repeat. Repeat. Repeat.') == 
      'Repeat');
  assert(sentenceWithMostUniqueWords('   ') == 
      'Error: Input string is empty.');
  assert(sentenceWithMostUniqueWords('First. Second! Third? Fourth.') == 
      'First');
  assert(sentenceWithMostUniqueWords('Hello, hello, HELLO. Different words here.') == 
      'Different words here');
}

void main() {
  testSentenceWithMostUniqueWords();
}

