// canRepaintStones determines if it's possible to repaint the stones such that
// no two adjacent stones have the same color. The stones are represented as a
// string where '.' represents an unpainted stone that can be repainted to 'R', 'G', or 'B'.
// The function uses a recursive backtracking approach to try all valid colorings.
String canRepaintStones(int n, String stones) {
  // isValid checks if the current stone arrangement has no adjacent stones of the same color
  bool isValid(List<String> stoneArray) {
    for (int i = 1; i < stoneArray.length; i++) {
      if (stoneArray[i] == stoneArray[i - 1]) {
        return false;
      }
    }
    return true;
  }

  // solve is a helper function that implements the recursive backtracking solution
  bool solve(List<String> stones, int idx) {
    if (idx == n) {
      return isValid(stones);
    }

    if (stones[idx] != '.') {
      return solve(stones, idx + 1);
    }

    for (String color in ["R", "G", "B"]) {
      stones[idx] = color;
      if (idx > 0 && stones[idx] == stones[idx - 1]) {
        continue;
      }
      if (solve(stones, idx + 1)) {
        return true;
      }
    }
    stones[idx] = '.';
    return false;
  }

  return solve(stones.split(''), 0) ? "Yes" : "No";
}

assertions:
void testCanRepaintStones() {
  assert(canRepaintStones(3, "R.G") == "Yes");
  assert(canRepaintStones(5, "R..G.") == "Yes");
  assert(canRepaintStones(4, "R.G.") == "Yes");
  assert(canRepaintStones(3, "RRG") == "No");
  assert(canRepaintStones(2, "..") == "Yes");
  assert(canRepaintStones(1, ".") == "Yes");
  assert(canRepaintStones(6, "R.G.B.") == "Yes");
}

void main() {
  testCanRepaintStones();
  print("All tests passed!");
}

