// findFixedPoint finds a fixed point in a sorted array of distinct integers.
// A fixed point is an index where arr[i] == i.
// The function uses binary search for efficient O(log n) time complexity.
// Returns the index of the fixed point, or -1 if no fixed point exists.
int findFixedPoint(List<int> arr) {
  int left = 0;
  int right = arr.length - 1;

  while (left <= right) {
    int mid = (left + right) ~/ 2;  // Integer division in Dart
    if (arr[mid] == mid) {
      return mid;
    } else if (arr[mid] < mid) {
      left = mid + 1;
    } else {
      right = mid - 1;
    }
  }

  return -1;
}

assertions:
void testFindFixedPoint() {
  assert(findFixedPoint([-10, -5, 0, 3, 7]) == 3);
  assert(findFixedPoint([0, 2, 5, 8, 17]) == 0);
  assert(findFixedPoint([-10, -5, 3, 4, 7, 9]) == -1);
  assert(findFixedPoint([]) == -1);
  assert(findFixedPoint([0]) == 0);
  assert(findFixedPoint([-1, 0, 2]) == 2);
  assert(findFixedPoint([-5, 1, 3, 4]) == 1);
}

void main() {
  testFindFixedPoint();
}

