// busiestHour finds the timestamp when the maximum occupancy occurs based on a list of occupancy change records.
// It processes each record in order, tracking the current and maximum occupancy.
// The timestamp of the record that first achieves the maximum occupancy is returned.
String busiestHour(List<MapEntry<String, int>> records) {
  int maxOccupancy = 0;
  int currentOccupancy = 0;
  String maxOccupancyTimestamp = "";

  for (var record in records) {
    currentOccupancy += record.value;
    if (currentOccupancy > maxOccupancy) {
      maxOccupancy = currentOccupancy;
      maxOccupancyTimestamp = record.key;
    }
  }

  return maxOccupancyTimestamp;
}

assertions:
void testBusiestHour() {
  // Test case 1: Simple case with clear maximum
  var testCase1 = [
    MapEntry("09:00", 5),
    MapEntry("10:00", 3),
    MapEntry("11:00", -2),
    MapEntry("12:00", 7),
    MapEntry("13:00", -4),
  ];
  assert(busiestHour(testCase1) == "12:00");

  // Test case 2: Multiple entries with same maximum occupancy
  var testCase2 = [
    MapEntry("08:00", 10),
    MapEntry("09:00", -5),
    MapEntry("10:00", 5),
    MapEntry("11:00", -5),
    MapEntry("12:00", 5),
  ];
  assert(busiestHour(testCase2) == "08:00");

  // Test case 3: Empty list
  var testCase3 = <MapEntry<String, int>>[];
  assert(busiestHour(testCase3) == "");

  // Test case 4: Decreasing occupancy
  var testCase4 = [
    MapEntry("09:00", -5),
    MapEntry("10:00", -3),
    MapEntry("11:00", -2),
  ];
  assert(busiestHour(testCase4) == "");
}

void main() {
  testBusiestHour();
  print("All tests passed!");
}

