// findMaxAverageSum calculates the maximum average sum of k consecutive elements in an array.
// It uses the sliding window technique to efficiently compute the sums of consecutive elements.
// If the array has fewer than k elements, it returns -1.
double findMaxAverageSum(List<int> arr, int k) {
  int n = arr.length;
  if (n < k) {
    return -1;
  }

  // Calculate the sum of first 'k' elements
  int currentSum = arr.sublist(0, k).reduce((a, b) => a + b);
  int maxSum = currentSum;

  // Use sliding window technique to find the max sum of 'k' consecutive elements
  for (int i = k; i < n; i++) {
    currentSum += arr[i] - arr[i - k];
    if (currentSum > maxSum) {
      maxSum = currentSum;
    }
  }

  return maxSum / k;
}

assertions:
void testFindMaxAverageSum() {
  // Test case 1: Normal case with k smaller than array length
  assert(findMaxAverageSum([1, 12, -5, -6, 50, 3], 4) == 12.75);

  // Test case 2: k equals array length
  assert(findMaxAverageSum([5, 7, 3, 2], 4) == 4.25);

  // Test case 3: k larger than array length
  assert(findMaxAverageSum([10, 20, 30], 4) == -1.0);

  // Test case 4: All negative numbers
  assert(findMaxAverageSum([-1, -2, -3, -4, -5], 2) == -1.5);

  // Test case 5: Single element array with k=1
  assert(findMaxAverageSum([8], 1) == 8.0);
}

void main() {
  testFindMaxAverageSum();
}

