// sortStoryEntries sorts a list of story entries by their timestamp.
// Each entry is expected to be a string where the first 19 characters represent
// the timestamp and the remaining part represents the text line.
// The function returns a list of text lines sorted by their timestamps.
List<String> sortStoryEntries(int n, List<String> entries) {
  // Parse each entry into a tuple of (timestamp, text)
  List<MapEntry<String, String>> parsedEntries = [];
  for (String entry in entries) {
    String timestamp = entry.substring(0, 19);
    String text = entry.substring(20);
    parsedEntries.add(MapEntry(timestamp, text));
  }

  // Sort entries by timestamp
  parsedEntries.sort((a, b) => a.key.compareTo(b.key));

  // Extract and return just the sorted text lines
  return parsedEntries.map((entry) => entry.value).toList();
}

assertions:
void testSortStoryEntries() {
  // Test Case 1: Simple case with 3 entries
  List<String> entries1 = [
    '2023-01-01 12:00:00 This is the first entry',
    '2023-01-01 11:00:00 This is the second entry',
    '2023-01-01 13:00:00 This is the third entry'
  ];
  assert(sortStoryEntries(3, entries1) == [
    'This is the second entry',
    'This is the first entry',
    'This is the third entry'
  ]);

  // Test Case 2: Case with different dates
  List<String> entries2 = [
    '2023-01-02 00:00:00 New Year second day',
    '2023-01-01 00:00:00 New Year first day',
    '2023-01-03 00:00:00 New Year third day'
  ];
  assert(sortStoryEntries(3, entries2) == [
    'New Year first day',
    'New Year second day',
    'New Year third day'
  ]);

  // Test Case 3: Single entry case
  List<String> entries3 = [
    '2023-12-31 23:59:59 Last moment of the year'
  ];
  assert(sortStoryEntries(1, entries3) == [
    'Last moment of the year'
  ]);

  // Test Case 4: Empty list case
  List<String> entries4 = [];
  assert(sortStoryEntries(0, entries4) == []);
}

void main() {
  testSortStoryEntries();
  print('All tests passed!');
}

