// ProductOfArray calculates the product of elements in a given range of an array.
// It uses a prefix product array to allow O(1) range product queries.
type ProductOfArray(nums: int array) =
    // Initialize the cumulative product array with an extra first element as 1 for easier calculation
    let prefixProducts = 
        let products = Array.zeroCreate (nums.Length + 1)
        products.[0] <- 1
        for i in 0 .. nums.Length - 1 do
            products.[i + 1] <- products.[i] * nums.[i]
        products

    // product returns the product of all elements in the array within the range [left, right], inclusive.
    // It runs in O(1) time.
    member this.Product(left: int, right: int) =
        prefixProducts.[right + 1] / prefixProducts.[left]

