open System

// matrixMultiplication multiplies two matrices A and B where A is of size NxM and B is of size MxP.
// The function returns the resultant matrix C of size NxP.
let matrixMultiplication (A: int[,]) (B: int[,]) (N: int) (M: int) (P: int) =
    // Initialize matrix C with zeros
    let C = Array2D.zeroCreate<int> N P

    // Perform matrix multiplication
    for i in 0..N-1 do
        for j in 0..P-1 do
            for k in 0..M-1 do
                C.[i, j] <- C.[i, j] + A.[i, k] * B.[k, j]
    C

// Dynamic programming solution to find the maximum sum of skills that does not exceed a given limit k.
// The function takes the number of skills (n), the maximum allowed sum (k), and an array of skill values.
// It returns the maximum sum of skills that can be achieved without exceeding k.
let findMaxSkillSum (n: int) (k: int) (skills: int array) =
    let dp = Array.zeroCreate (k + 1)

    for skill in skills do
        for j in k .. -1 .. skill do
            dp.[j] <- max dp.[j] (dp.[j - skill] + skill)

    dp.[k]

