open System

// Converts a time string in "HH:MM" format to minutes since midnight for easier comparison.
let timeToMinutes (time: string) =
    let parts = time.Split(':')
    int parts.[0] * 60 + int parts.[1]

// Converts minutes since midnight back to "HH:MM" format.
let minutesToTime (minutes: int) =
    sprintf "%02d:%02d" (minutes / 60) (minutes % 60)

// findFreeTimeSlots returns a list of free time slots not covered by the given meetings within the working day 09:00 to 17:00.
// Parameters:
//   meetings: String containing a series of meetings in the format "HH:MM-HH:MM" separated by commas.
// Returns:
//   List of tuples where each tuple represents a free time slot in the format ("HH:MM", "HH:MM").
let findFreeTimeSlots (meetings: string) =
    let workStart = "09:00"
    let workEnd = "17:00"

    // Split the meetings into start and end times tuples
    let meetingsIntervals =
        meetings.Split([|", "|], StringSplitOptions.RemoveEmptyEntries)
        |> Array.map (fun meeting -> 
            let parts = meeting.Split('-')
            (parts.[0], parts.[1]))

    // Convert all times to minutes for easier comparison
    let meetingsInMinutes =
        meetingsIntervals
        |> Array.map (fun (start, end_) -> (timeToMinutes start, timeToMinutes end_))
        |> Array.sortBy fst

    // Add the working hours to the interval list
    let workStartMinutes = timeToMinutes workStart
    let workEndMinutes = timeToMinutes workEnd
    let allIntervals =
        Array.append [| (workStartMinutes, workStartMinutes) |] meetingsInMinutes
        |> Array.append [| (workEndMinutes, workEndMinutes) |]
        |> Array.sortBy fst

    // Find free slots between meetings
    let freeSlots =
        allIntervals
        |> Array.pairwise
        |> Array.choose (fun ((_, endCurrent), (startNext, _)) ->
            if endCurrent < startNext then
                Some (minutesToTime endCurrent, minutesToTime startNext)
            else
                None)

    List.ofArray freeSlots

