module FactorialLastDigit =

    // calculateLastDigitOfFactorial computes the last digit of N factorial when N is less than 5.
    // For N >= 5, it returns 0 since factorial of numbers >=5 ends with 0.
    let calculateLastDigitOfFactorial (n: int) =
        if n >= 5 then
            0
        else
            let rec computeFactorial acc i =
                if i > n then acc
                else computeFactorial (acc * i) (i + 1)
            (computeFactorial 1 1) % 10

