// maxSumAfterSwap calculates the maximum path sum in a binary tree after trying all possible child swaps.
// The tree is represented as a list of nodes where each node has a parent and two children (left and right).
// The function uses depth-first search (DFS) to traverse the tree and calculate path sums.
let maxSumAfterSwap (n: int) (nodes: (int * int * int) list) =
    // Create a dictionary to map each parent node to its left and right children
    let children = Dictionary<int, int * int>()
    for (parent, left, right) in nodes do
        children.[parent] <- (left, right)

    // Recursive DFS function to calculate the maximum path sum from a given node
    let rec dfs (node: int) =
        if node = -1 then
            0
        else
            let left, right = 
                if children.ContainsKey(node) then 
                    children.[node] 
                else 
                    (-1, -1)
            let leftSum = dfs left
            let rightSum = dfs right
            node + max leftSum rightSum

    // Calculate the maximum sum for the original tree
    let originalMaxSum = dfs 1
    let mutable maxSum = originalMaxSum

    // Try swapping every node's children and calculate the new maximum sum
    for KeyValue(parent, (left, right)) in children do
        // Swap children
        children.[parent] <- (right, left)
        let newSum = dfs 1
        maxSum <- max maxSum newSum
        // Revert the swap
        children.[parent] <- (left, right)

    maxSum

