// climbStairs calculates the number of distinct ways to climb a staircase with n steps,
// where you can take either 1 or 2 steps at a time.
// This function uses dynamic programming to efficiently compute the result.
let climbStairs (n: int) =
    if n = 1 then
        1
    elif n = 2 then
        2
    else
        let dp = Array.zeroCreate (n + 1)
        dp.[1] <- 1
        dp.[2] <- 2

        for i in 3 .. n do
            dp.[i] <- dp.[i - 1] + dp.[i - 2]

        dp.[n]

