// findOverdueTasks identifies overdue tasks from a list of tasks and calculates how many days they are overdue.
// A task is overdue if:
// - It is pending and the due date is before the current date.
// - It is completed but the completion date is after the due date.
let findOverdueTasks (tasks: Task list) (currentDate: string) =
    let currentDateObj = parseDate currentDate

    tasks
    |> List.choose (fun task ->
        let dueDateObj = parseDate task.dueDate

        if task.status = "pending" && dueDateObj < currentDateObj then
            let daysOverdue = (currentDateObj - dueDateObj).Days
            Some { taskId = task.taskId; daysOverdue = daysOverdue }
        elif task.status = "completed" then
            let completionDateObj = parseDate task.completionDate
            if completionDateObj > dueDateObj then
                let daysOverdue = (completionDateObj - dueDateObj).Days
                Some { taskId = task.taskId; daysOverdue = daysOverdue }
            else
                None
        else
            None
    )

