// Converts a string representation of a cable length to centimeters (as an integer).
// The input string can be in the format "X" (meters) or "X.YY" (meters and centimeters).
let convertToCm (s: string) =
    if s.Contains(".") then
        let parts = s.Split('.')
        let whole = parts.[0]
        let decimal = parts.[1].PadRight(2, '0').Substring(0, 2)
        int whole * 100 + int decimal
    else
        int s * 100

// Finds the maximum length in centimeters from a list of cable lengths.
let findMaxCm (cables: int list) =
    List.fold (fun maxCm cm -> if cm > maxCm then cm else maxCm) 0 cables

// Performs a binary search to find the maximum length of cable pieces of size 'mid'
// that can be cut from the given cables to get at least 'k' pieces.
let binarySearch (cables: int list) (k: int) (maxCm: int) =
    let rec search (left: int) (right: int) (ans: int) =
        if left > right then ans
        else
            let mid = (left + right) / 2
            let total =
                List.sumBy (fun cable -> cable / mid) cables
            if total >= k then
                search (mid + 1) right mid
            else
                search left (mid - 1) ans
    search 1 maxCm 0

// Formats the result in meters with two decimal places.
let formatResult (ans: int) =
    if ans = 0 then "0.00"
    else
        let meters = float ans / 100.0
        meters.ToString("0.00")

