// Function to calculate the maximum sum of non-overlapping subarrays of length K
// Parameters:
//   N: int - Length of the array A
//   K: int - Length of the subarrays
//   A: int array - Input array of integers
// Returns:
//   int - Maximum sum of non-overlapping subarrays of length K
let maxSumOfNonOverlappingSubarrays (N: int) (K: int) (A: int array) =
    if K > N then
        0
    else
        let mutable currentSum = A.[0..K-1] |> Array.sum
        let mutable maxSum = currentSum

        for i in K .. N - 1 do
            currentSum <- currentSum + A.[i] - A.[i - K]
            maxSum <- max maxSum currentSum

        maxSum

