// It then computes prefix sums to get the final result array.
let computeFinalArray (n: int) (operations: (int * int * int) list) =
    // Initialize difference arrays with an extra space to avoid index issues
    let diff1 = Array.create (n + 2) 0
    let diff2 = Array.create (n + 2) 0

    // Process each operation to update the difference arrays
    for (l, r, v) in operations do
        // Update for sum_vq (v * i)
        diff1.[l] <- diff1.[l] + v
        if r + 1 <= n then
            diff1.[r + 1] <- diff1.[r + 1] - v

        // Update for sum_other (-v * (l-1))
        let term = -v * (l - 1)
        diff2.[l] <- diff2.[l] + term
        if r + 1 <= n then
            diff2.[r + 1] <- diff2.[r + 1] - term

    // Compute prefix sums for sum_vq
    let sumVq = Array.create (n + 1) 0
    let mutable current = 0
    for i in 1 .. n do
        current <- current + diff1.[i]
        sumVq.[i] <- current

    // Compute prefix sums for sum_other
    let sumOther = Array.create (n + 1) 0
    let mutable currentOther = 0
    for i in 1 .. n do
        currentOther <- currentOther + diff2.[i]
        sumOther.[i] <- currentOther

    // Compute the final result array
    let result = 
        [| for i in 1 .. n do
            yield sumVq.[i] * i + sumOther.[i] |]

    result

