    // minScarecrows calculates the minimum number of scarecrows needed to protect all crops (represented by 1s)
    // in an m x n farmland grid. A scarecrow protects its cell and all 4 adjacent cells (up, down, left, right).
    // The function returns the count of scarecrows placed.
    let minScarecrows (m: int) (n: int) (farmland: int[,]) =
        let mutable scarecrows = 0
        let protectedCells = Array2D.create m n false

        for i in 0..m-1 do
            for j in 0..n-1 do
                if farmland.[i,j] = 1 && not protectedCells.[i,j] then
                    scarecrows <- scarecrows + 1
                    // Protect this cell and adjacent cells (if within bounds)
                    for di, dj in [(0,0); (1,0); (0,1); (-1,0); (0,-1)] do
                        let ni, nj = i + di, j + dj
                        if ni >= 0 && ni < m && nj >= 0 && nj < n then
                            protectedCells.[ni,nj] <- true
        scarecrows
