open System

// Represents a 2D point with integer coordinates
type Point = { X: int; Y: int }

// calculateSquaredDistance computes the squared distance between two points.
// This avoids floating point operations and potential precision issues.
let calculateSquaredDistance (p1: Point) (p2: Point) =
    let dx = p1.X - p2.X
    let dy = p1.Y - p2.Y
    dx * dx + dy * dy

// isColinear checks if three points are colinear by calculating the area of the triangle they form.
// If the area is zero, the points are colinear.
let isColinear (a: Point) (b: Point) (c: Point) =
    (b.X - a.X) * (c.Y - a.Y) - (b.Y - a.Y) * (c.X - a.X) = 0

// isIsoscelesTriangle checks if a triangle formed by three points is isosceles.
// An isosceles triangle has at least two sides of equal length.
let isIsoscelesTriangle (a: Point) (b: Point) (c: Point) =
    let d1 = calculateSquaredDistance a b
    let d2 = calculateSquaredDistance b c
    let d3 = calculateSquaredDistance c a
    d1 = d2 || d2 = d3 || d3 = d1

// countIsoscelesTriangles counts the number of isosceles triangles that can be formed
// from all possible combinations of three points in an N x M grid.
let countIsoscelesTriangles (N: int) (M: int) =
    let points = [| for x in 0 .. N - 1 do for y in 0 .. M - 1 do yield { X = x; Y = y } |]
    let mutable count = 0

    for i in 0 .. points.Length - 1 do
        for j in i + 1 .. points.Length - 1 do
            for k in j + 1 .. points.Length - 1 do
                let a = points.[i]
                let b = points.[j]
                let c = points.[k]
                if not (isColinear a b c) && isIsoscelesTriangle a b c then
                    count <- count + 1
    count

