// countCharacters counts the occurrences of each lowercase letter in a string.
// It returns an array of 26 integers, where each index corresponds to a letter ('a' to 'z').
let countCharacters (s: string) =
    let counts = Array.zeroCreate 26
    for c in s do
        let index = int c - int 'a'
        if index >= 0 && index < 26 then
            counts.[index] <- counts.[index] + 1
    counts

// buildSortedString constructs a new string by appending each lowercase letter
// in alphabetical order, repeated according to its count in the counts array.
let buildSortedString (counts: int array) =
    let mutable res = System.Text.StringBuilder()
    for i in 0 .. 25 do
        let c = char (int 'a' + i)
        res.Append(c, counts.[i]) |> ignore
    res.ToString()

