// solvePaintingProblem calculates the minimum number of brush changes required to paint a sequence of colors.
// The problem is solved using dynamic programming where the state is represented by the colors on the two brushes.
let solvePaintingProblem (colors: int array) =
    let mutable prevDp = Dictionary<(int * int), int>()
    prevDp.Add((0, 0), 0) // Initial state: both brushes are 0 (no color)

    for c in colors do
        let currentDp = Dictionary<(int * int), int>()

        for kvp in prevDp do
            let (a, b), cost = kvp.Key, kvp.Value

            // Option 1: Use the first brush (a)
            let newA1, newB1, newCost1 = 
                if a <> c then (c, b, cost + 1)
                else (a, b, cost)
            let newState1 = if newA1 <= newB1 then (newA1, newB1) else (newB1, newA1)
            if not (currentDp.ContainsKey(newState1)) || newCost1 < currentDp.[newState1] then
                currentDp.[newState1] <- newCost1

            // Option 2: Use the second brush (b)
            let newA2, newB2, newCost2 = 
                if b <> c then (a, c, cost + 1)
                else (a, b, cost)
            let newState2 = if newA2 <= newB2 then (newA2, newB2) else (newB2, newA2)
            if not (currentDp.ContainsKey(newState2)) || newCost2 < currentDp.[newState2] then
                currentDp.[newState2] <- newCost2

        prevDp <- currentDp

    if prevDp.Count > 0 then
        prevDp.Values |> Seq.min
    else
        0

