// rollDiceAndTrack simulates rolling two dice a specified number of times and tracks the frequency of each possible total (2-12).
// numRolls: The number of times to roll the two dice. Must be a positive integer.
// Returns a map (dictionary) of totals to their frequencies.
let rollDiceAndTrack (numRolls: int) =
    if numRolls <= 0 then
        raise (ArgumentException("numRolls must be a positive integer."))

    let frequency = [| for _ in 2..12 -> 0 |] // Index 0 corresponds to total 2, index 10 to total 12.
    let rnd = Random()

    for i in 1..numRolls do
        let die1 = rnd.Next(1, 7)
        let die2 = rnd.Next(1, 7)
        let total = die1 + die2
        frequency.[total - 2] <- frequency.[total - 2] + 1

        printfn "Roll %d: (%d, %d), Total: %d" i die1 die2 total

    printfn "\nFrequency of Totals After %d Rolls:" numRolls
    for total in 2..12 do
        printfn "%d: %d" total frequency.[total - 2]

    frequency
open System
open System.Diagnostics

module TestDiceRolls =
    let testRollDiceAndTrack() =
        // Since the function uses random numbers, we can't test exact values
        // Instead we'll test some basic properties of the results

        // Test with 10 rolls
        let result10 = rollDiceAndTrack 10
        Debug.Assert(Array.sum result10 = 10)
        Debug.Assert(result10.Length = 11) // 2-12 totals

        // Test with 20 rolls
        let result20 = rollDiceAndTrack 20
        Debug.Assert(Array.sum result20 = 20)
        Debug.Assert(result20.Length = 11)

        // Test with 50 rolls
        let result50 = rollDiceAndTrack 50
        Debug.Assert(Array.sum result50 = 50)
        Debug.Assert(result50.Length = 11)

        // Test exception for invalid input
        try
            rollDiceAndTrack 0 |> ignore
            Debug.Assert(false, "Should have thrown exception for numRolls <= 0")
        with
        | :? ArgumentException -> Debug.Assert(true)
        | _ -> Debug.Assert(false, "Wrong exception type thrown")

